commits.content:
body { font-family: system-ui, -apple-system, Segoe UI, Roboto, sans-serif; background:#f7f7f7; }
.container { min-height:100vh; display:grid; place-items:center; }
.card { background:#fff; padding:16px; border:1px solid #eee; border-radius:10px; display:grid; gap:10px; width:min(480px, 92vw); }
.error { background:#ffecec; color:#b10000; padding:8px; border-radius:8px; }
.topbar { background:#111; color:#fff; display:flex; justify-content:space-between; align-items:center; padding:10px 16px; }
.topbar a { color:#ddd; margin-right:12px; text-decoration:none; }
.topbar a.active { color:#fff; font-weight:600; }
.page { padding:16px; max-width:1000px; margin:0 auto; }
.row { display:flex; justify-content:space-between; align-items:center; }
.table { width:100%; border-collapse:collapse; background:#fff; border:1px solid #eee; border-radius:10px; overflow:hidden; }
.table th, .table td { padding:10px; border-bottom:1px solid #f0f0f0; }
.btn { background:#111; color:#fff; padding:8px 12px; border-radius:8px; text-decoration:none; }
input, select, button { padding:10px; border:1px solid #ddd; border-radius:8px; }
button { background:#111; color:#fff; cursor:pointer; }
